import './DebitForm.css';
import {useState} from "react";

const DebitForm = () => {

    const [errorCVV, setErrorCVV] = useState(false);
    const [errorName, setErrorName] = useState(false);
    const [errorCard, setErrorCard] = useState(false);
    const [errorDate, setErrorDate] = useState(false);

    const cvvHandler = (e) => {
        const { value } = e.target;

        if (value.length < 3) {
            setErrorCVV(true);
        } else {
            setErrorCVV(false);
        }
    }

    const dateHandler = (e) => {
        const { value } = e.target;

        let date = new Date(value);
        let currDate = new Date();
        if (currDate > date) {
            setErrorDate(true);
        } else {
            setErrorDate(false);
        }
    }

    const cardHandler = (e) => {
        const { value } = e.target;
        if (value.length !== 16 && isNaN(value)) {
            setErrorCard(true);
        } else {
            setErrorCard(false);
        }
    }

    const nameHandler = (e) => {
        const { value } = e.target;

        if (value.length < 2) {
            setErrorName(true);
        } else {
            setErrorName(false);
        }
    }

    return (
        <div className='debit-form_block'>
            <div className='debit-form_inner'>
                <form className='debit-form'>
                    <label className='debit-input-text'>
                        Card number
                        <input className='debit-input' type='text' required onChange={cardHandler}/>
                        {errorCard ? <p className='error-text'>Card number should be 16 size long</p> : null}
                    </label>
                    <label className='debit-input-text'>
                        Valid thru
                        <input className='debit-input' type='date' required onChange={dateHandler}/>
                        {errorDate ? <p className='error-text'>Date is expired or not entered</p> : null}
                    </label>
                    <label className='debit-input-text'>
                        CVV
                        <input className='debit-input' type='text' required onChange={cvvHandler}/>
                        {errorCVV ? <p className='error-text'>CVV includes 3 letters</p> : null}
                    </label>
                    <label className='debit-input-text'>
                        Cardholder name
                        <input className='debit-input' type='text' required onChange={nameHandler}/>
                        {errorName ? <p className='error-text'>Name should contain at least 2 letters</p> : null}
                    </label>
                    <button type='submit'>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default DebitForm;
