import './DebitForm.css';
import {useState} from "react";

const DebitForm = () => {

    const [error, setError] = useState(false);

    const cvvHandler = (e) => {
        const { value } = e.target;

        if (value.length > 3) {
            setError(true);
        } else {
            setError(false);
        }
    }

    return (
        <div className='debit-form_block'>
            <div className='debit-form_inner'>
                <form className='debit-form'>
                    <label className='debit-input-text'>
                        Card number
                        <input className='debit-input' type='text' required/>
                        {error ? <p className='error-text'>Card number should be 16 size long</p> : null}
                    </label>
                    <label className='debit-input-text'>
                        Valid thru
                        <input className='debit-input' type='date' required/>
                        {error ? <p className='error-text'>Date is expired</p> : null}
                    </label>
                    <label className='debit-input-text'>
                        CVV
                        <input className='debit-input' type='text' required onChange={cvvHandler}/>
                        {error ? <p className='error-text'>CVV includes 3 letters</p> : null}
                    </label>
                    <label className='debit-input-text'>
                        Cardholder name
                        <input className='debit-input' type='text' required/>
                        {error ? <p className='error-text'>Name should contain at least 2 letters</p> : null}
                    </label>
                    <button type='submit'>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default DebitForm;
